import { useState, useEffect } from "react";
import {useNavigate, useParams} from 'react-router-dom'

const Editkamar = () => {
  const [kamar, settittle] = useState("");
  const [harga, setprice] = useState(0);
  const navigate = useNavigate();
  const {id} = useParams();


  useEffect(()=> {
    getproductbyid()
  })


  const getproductbyid = async() => {
    const response = await fetch(`http://localhost:8080/kamar/${id}`);
    const data = response.json();
    settittle(data.kamar);
    setprice(data.harga)
  }

  const saveproduct = async (e) => {
    e.preventDefault();
    const product = { kamar, harga };
    await fetch("http://localhost:8080/kamar", {
      method: "POST",
      body: JSON.stringify(product),
      headers: {
        "Content-type": "application/json",
      },
    });
    navigate('/umar')
  };

  return (
    <div>
      <form onSubmit={saveproduct}>
        <div className="field">
          <label className="label">Title</label>
          <div className="control">
            <input
              className="input"
              value={kamar}
              onChange={(e) => settittle(e.target.value)}
              type="text"
              placeholder="Title"
            />
          </div>
        </div>

        <div className="field">
          <label className="label">Price</label>
          <div className="control">
            <input
              className="input"
              value={harga}
              onChange={(e) => setprice(e.target.value)}
              type="text"
              placeholder="Price"
            />
          </div>
        </div>

        <div className="field">
          <div className="control">
            <button className="button is-primary">UPDATE</button>
          </div>
        </div>
      </form>
    </div>
  );
};

export default Editkamar;
